    <p>
      &#25991;&#20214; head-common.S &#23450;&#20041;&#20102;_mmap_switched&#20989;&#25968;
    </p>
    <p>
      __mmap_switched:
    </p>
    <p>
      &#160;&#160;&#160;&#160;adr r3, __mmap_switched_data
    </p>
    <p>
      
    </p>
    <p>
      &#160;&#160;&#160;&#160;ldmia&#160;&#160;&#160;r3!, {r4, r5, r6, r7}
    </p>
    <p>
      &#160;&#160;&#160;&#160;cmp r4, r5&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Copy data segment if needed
    </p>
    <p>
      1:&#160;&#160;cmpne&#160;&#160;&#160;r5, r6
    </p>
    <p>
      &#160;&#160;&#160;&#160;ldrne&#160;&#160;&#160;fp, [r4], #4
    </p>
    <p>
      &#160;&#160;&#160;&#160;strne&#160;&#160;&#160;fp, [r5], #4
    </p>
    <p>
      &#160;&#160;&#160;&#160;bne 1b
    </p>
    <p>
      
    </p>
    <p>
      &#160;&#160;&#160;&#160;mov fp, #0&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Clear BSS (and zero fp)
    </p>
    <p>
      1:&#160;&#160;cmp r6, r7
    </p>
    <p>
      &#160;&#160;&#160;&#160;strcc&#160;&#160;&#160;fp, [r6],#4
    </p>
    <p>
      &#160;&#160;&#160;&#160;bcc 1b
    </p>
    <p>
      
    </p>
    <p>
      &#160;ARM(&#160;&#160;&#160;ldmia&#160;&#160;&#160;r3, {r4, r5, r6, r7, sp})
    </p>
    <p>
      &#160;THUMB( ldmia&#160;&#160;&#160;r3, {r4, r5, r6, r7}&#160;&#160;&#160;&#160;)
    </p>
    <p>
      &#160;THUMB( ldr sp, [r3, #16]&#160;&#160;&#160;&#160;&#160;&#160;&#160;)
    </p>
    <p>
      &#160;&#160;&#160;&#160;str r9, [r4]&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Save processor ID
    </p>
    <p>
      &#160;&#160;&#160;&#160;str r1, [r5]&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Save machine type
    </p>
    <p>
      &#160;&#160;&#160;&#160;str r2, [r6]&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Save atags pointer
    </p>
    <p>
      &#160;&#160;&#160;&#160;cmp r7, #0
    </p>
    <p>
      &#160;&#160;&#160;&#160;strne&#160;&#160;&#160;r0, [r7]&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ Save control register values
    </p>
    <p>
      &#160;&#160;&#160;&#160;<font color="#f00f0f">b&#160;&#160;&#160;start_kernel&#160;&#160;&#35774;&#32622;&#22909;&#26632;&#39030;&#25351;&#38024;&#23601;&#21487;&#20197;&#21551;&#21160;start_kernel&#20989;&#25968;&#20102; </font>
    </p>
    <p>
      <b>&#20989;&#25968;&#23450;&#20041;&#65306; asmlinkage __visible void __init start_kernel(void)</b>
    </p>
    <p>
      ENDPROC(__mmap_switched)
    </p>
    <p>
      
    </p>
    <p>
      &#160;&#160;&#160;&#160;.align&#160;&#160;2
    </p>
    <p>
      &#160;&#160;&#160;&#160;.type&#160;&#160;&#160;__mmap_switched_data, %object
    </p>
    <p>
      __mmap_switched_data:
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;__data_loc&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r4
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;_sdata&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r5
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;__bss_start&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r6
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;_end&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r7
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;processor_id&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r4
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;__machine_arch_type&#160;&#160;&#160;&#160;&#160;@ r5
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;__atags_pointer&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r6
    </p>
    <p>
      #ifdef CONFIG_CPU_CP15
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;cr_alignment&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r7
    </p>
    <p>
      #else
    </p>
    <p>
      &#160;&#160;&#160;&#160;.long&#160;&#160;&#160;0&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;&#160;@ r7
    </p>
    <p>
      #endif
    </p>
    <p>
      &#160;&#160;<b><i><font color="#0c36f0">&#160;&#160;.long&#160;&#160;&#160;init_thread_union + THREAD_START_SP @ sp </font></i></b>
    </p>
    <p>
